mapper.select:
SELECT user_id, username, email, password, role, is_active, created_at, updated_at
FROM users
WHERE username = #{username}

SELECT user_id, username, email, password, role, is_active, created_at, updated_at
FROM users
WHERE email = #{email}

SELECT user_id, username, email, password, role, is_active, created_at, updated_at
FROM users
WHERE user_id = #{userId}

SELECT COUNT(*) > 0
FROM users
WHERE username = #{username}

SELECT COUNT(*) > 0
FROM users
WHERE email = #{email}
